mapper.select:
select page_2.* from (select page_1.*, rownum as page_num from (
select yycgdmx.id as yycgdmxid, useryy.id as useryyid, useryy.mc as useryymc, ypxx.id, ypxx.bm, ypxx.mc, ypxx.jx, ypxx.zbjg, ypxx.gg, ypxx.zhxs, ypxx.scqymc, ypxx.spmc, ypxx.jyzt, (select info from dictinfo where ypxx.jyzt = dictinfo.dictcode and dictinfo.typecode = '003') as jyztmc,
yycgdmx.cgl, yycgdmx.jyjg, yycgdmx.cgje, yycgdmx.cgzt, (select info from dictinfo where yycgdmx.cgzt = dictinfo.dictcode and dictinfo.typecode = '011') as cgztmc, usergys.mc as usergysmc, usergys.id as usergysid
from yycgdmx${businessyear} yycgdmx, yycgd${businessyear} yycgd, useryy, ypxx, usergys where yycgdmx.yycgdid = yycgd.id and yycgd.useryyid = useryy.id and yycgdmx.ypxxid = ypxx.id and yycgdmx.usergysid = usergys.id
) page_1
where rownum <= ${pageQuery.PageQuery_end}) page_2 where page_2.page_num >= ${pageQuery.PageQuery_start}

select page_2.* from (select page_1.*, rownum as page_num from (
select
gysypml.id as gysypmlid, gysypml.ypxxid, gysypml.usergysid, usergys.mc as usergysmc, gysypml_control.control, (select info from dictinfo where dictinfo.typecode = '008' and dictinfo.dictcode = gysypml_control.control) as controlmc, ypxx.id, ypxx.bm, ypxx.mc, ypxx.jx, ypxx.gg, ypxx.zhxs, ypxx.scqymc, ypxx.spmc, ypxx.zbjg, ypxx.jyzt, (select info from dictinfo where ypxx.jyzt = dictinfo.dictcode and dictinfo.typecode = '003') as jyztmc
from gysypml, usergys, gysypml_control, ypxx where gysypml.usergysid = usergys.id and gysypml.ypxxid = gysypml_control.ypxxid and gysypml.usergysid = gysypml_control.usergysid and gysypml.ypxxid = ypxx.id
and gysypml.usergysid in ( select usergysarea.usergysid from usergysarea where #{useryy.dq} like usergysarea.areaid || '%'
) and gysypml.ypxxid not in ( select yycgdmx.ypxxid from yycgdmx${businessyear} yycgdmx where yycgdmx.yycgdid=#{yycgdCustom.id} )
) page_1
where rownum <= ${pageQuery.PageQuery_end}) page_2 where page_2.page_num >= ${pageQuery.PageQuery_start}
